import React, { Component } from 'react'
import './LogIn.css'
import api from '../api/api'

class LogIn extends Component{
    constructor(props){
        super();
    
        this.state = {
            name: '',
            setName: props.setName,
        };
        this.handleChange = this.handleChange.bind(this);
        this.logIn = this.logIn.bind(this);
    }


    logIn(){
        this.state.setName(this.state.name)
    }


    handleChange(event) {
        this.setState({
            name: event.target.value,
        })  
    }
    
    render(){ 
        return(
            <div class = 'container'>
                <h1> Log In Type </h1>
                <div>
                    <ul>
                       <label>Username : </label>   
                        <input value = {this.state.credentials.usr} onChange={this.handleChange} type="text" placeholder="Enter Username" name="username" />  
                    </ul>
                    <button to ='/App'className = 'entry' onClick = {() => this.logIn()}>LogIn</button>
                </div>
            </div>
            );
        }
}


export default LogIn;
