import './App.css';
import React from 'react';
import io from "socket.io-client";
const ENDPOINT = "127.0.0.1:3001";


function Message(props){
    let messagesHTML = []; 

    for(let j = 0; j < props.messages.length; j++){ 
        let containerCss;

        if(props.messages[j].name === props.name){
            containerCss = "containerLeft"
        }
        else{
            containerCss = "containerRight"
        }

        messagesHTML[j] = <div className = {containerCss}>
                            <div className = "message">
                                <p>{props.messages[j].message}</p>
                                <span className = "UserName"> {props.messages[j].name} </span>
                            </div>
                    </div>
    }

    return (<div className = "board"> 
               {messagesHTML}
           </div> 
    )
}


class App extends React.Component {
    constructor(){
        super()

        let message = {
            name: 'Magnus',
            message: 'I hate css',
        }


        this.state = {
            socket: io(ENDPOINT),
            messages: new Array(),
            message: '',
            name: ''
        }

        this.SendMessage = this.SendMessage.bind(this)
    }

    InputOnChange(event){
        this.setState({
            name: event.target.value,
        })      
    }

    SendMessage(){
        this.state.socket.emit('SendMessage', ({messae: this.state.message, name: this.state.name}))

        this.setState({
            message: ''
        })
    }

    componentDidMount(){
        this.state.socket.on('NewMessage', (data) => {
            this.setState({
                messages: data
            })
        })
    }

    render(){
        let messages = <a>Loading</a>
        console.log(this.state.messages)
        if(this.state.messages.length !== 0){
            messages = <Message messages = {this.state.messages}></Message>
        }

        return (
            <div className="App">
                <header className="App-header">
                </header>
                    <div>
                        {messages}
                        <div>
                            <input value = {this.state.message} onChange={this.InputOnChange} type="text" placeholder="Enter Username" name="username" />
                            <button className = 'entry' onClick = {() => this.sendMessage()}>Send</button> 
                        </div>
                </div>
            </div>
        );  
    }
}

export default App;
