import React, { Component } from 'react'
import { Link } from 'react-router-dom';

class LogIn extends Component{
    constructor(props){
        super(props);
    
        this.state = {
            name: '',
            selectedRoom: null,
            setName: props.setName,
            loggedIn: false,
            socket: props.socket,
            rooms: null
        };

        this.handleChange = this.handleChange.bind(this);
        this.logIn = this.logIn.bind(this);
    }


    logIn(){
        this.state.setName({name: this.state.name, selectedRoom: this.state.selectedRoom})
        this.setState({
            loggedIn: true
        })
    }


    handleChangeRoomSelect(event) {
        this.setState({
            selectedRoom: event.target.value,
        })  
    }

    handleChange(event) {
        this.setState({
            name: event.target.value,
        })  
    }
    
    render(){ 
        let logIn = <button className = 'entry' onClick = {() => this.logIn()}>LogIn</button>
        let roomsHTML = [];

        if(this.state.rooms === null){   
            this.state.socket.emit('rooms', (response) => {
                this.setState({
                    rooms: response.rooms
                })
            })
        }

        if(this.state.rooms[0] !== ''){
            for(let i = 0; i < this.state.rooms.length; i ++){    
                roomsHTML[i] = <button value = {this.state.selectedRoom} onClick = {() => this.handleChangeRoomSelect}>{this.state.rooms[i]}</button>
            }
        }
        if(this.state.rooms[0] === ''){
            roomsHTML[0] = <a>No Rooms Available</a>
        }


        
        if(this.state.loggedIn){
            logIn = <Link to ='/App'> LOGIN</Link>
        }

        return(
            <div class = 'container'>
                <h1> Log In Type </h1>
                <div>
                    <ul>
                        <label>Username : </label>   
                        <input value = {this.state.name} onChange={this.handleChange} type="text" placeholder="Enter Username" name="username" />  
                        
                    </ul>
                    {logIn}
                </div>
            </div>
        );
    }
}


export default LogIn;
