import logo from './logo.svg';
import './App.css';
import React from 'react';
const ENDPOINT = "127.0.0.1:3001";


function Message(props){
    let messagesHTML = []; 

    for(let j = 0; j < props.messages.length; j++){ 
        messagesHTML[j] = <div class="container">
                        <p>{props.messages[j].message}</p>
                        <span className = "name"> {props.messages[j].name} </span>
                    </div>

    return <div className="board"> 
               {messagesHTML}
           </div> 
    }
}


class App extends React.Component {
    constructor(){
        super()

        this.state = {
            socket: io(ENDPOINT),
            messages: new Array(),
            message: ''
        }

        this.SendMessage = this.SendMessage.bind(this)
    }

    InputOnChange(event){
        this.setState({
            playerName: event.target.value,
        })      
    }

    SendMessage(){
        this.state.socket.emit('SendMessage', (this.state.message))
        this.setState({
            message: ''
        })
    }

    componentDidMount(){
        this.state.socket.on('NewMessage', (data) => {
            this.setState({
                messages: data
            })
        })
    }

    render(){
        return (
            <div className="App">
                <header className="App-header">
                    <div>
                        <Message messages = {this.state.messages}></Message>
                        <input value = {this.state.message} onChange={this.InputOnChange} type="text" placeholder="Enter Username" name="username" />
                        <button className = 'entry' onClick = {() => this.sendMessage()}>Send</button> 
                    </div>
                </header>
            </div>
        );  
    }
}

export default App;
