import './App.css';
import React from 'react';
import io from "socket.io-client";
const ENDPOINT = "127.0.0.1:3001";


function Message(props){
    let messagesHTML = []; 

    for(let j = 0; j < props.messages.length; j++){ 
        messagesHTML[j] = <div class="container">
                            <p>{props.messages[j].message}</p>
                            <span className = "name"> {props.messages[j].name} </span>
                    </div>

    return (<div className="container"> 
               {messagesHTML}
           </div> 
    )}
}


class App extends React.Component {
    constructor(){
        super()

        let message = {
            name: 'Magnus',
            message: 'I hate css'
        }


        this.state = {
            socket: io(ENDPOINT),
            messages: [message, message, message],
            message: ''
        }

        this.SendMessage = this.SendMessage.bind(this)
    }

    InputOnChange(event){
        this.setState({
            playerName: event.target.value,
        })      
    }

    SendMessage(){
        this.state.socket.emit('SendMessage', (this.state.message))
        this.setState({
            message: ''
        })
    }

    componentDidMount(){
        this.state.socket.on('NewMessage', (data) => {
            this.setState({
                messages: data
            })
        })
    }

    render(){
        let messages = <a>Loading</a>

        if(this.state.messages.length !== 0){
            messages = <Message messages = {this.state.messages}></Message>
        }

        return (
            <div className="App">
                <header className="App-header">
                    <div>
                        {messages}
                        <input value = {this.state.message} onChange={this.InputOnChange} type="text" placeholder="Enter Username" name="username" />
                        <button className = 'entry' onClick = {() => this.sendMessage()}>Send</button> 
                    </div>
                </header>
            </div>
        );  
    }
}

export default App;
